function checkIfDark(){let e=window.localStorage.getItem("dark");if(null!=e&&"true"==e){let t=document.querySelector("body"),l=document.querySelector(".toggle");t.classList.add("dark"),l?.classList.add("dark")}}function toggleBody(){let e=document.querySelector("body");e.classList.toggle("dark"),e.classList.contains("dark")?window.localStorage.setItem("dark",!0):window.localStorage.setItem("dark",!1)}function toggleHandler(){let e=document.querySelector(".toggle");e?.addEventListener("click",function(e){e.preventDefault(),this.classList.toggle("dark"),toggleBody()})}checkIfDark(),toggleHandler();